log(level, msg, meta, callback) {
    if (this.silent) {
      return callback(null, true);
    }
    const logEntry = this.defaultTransform(level, msg, Object.assign({}, meta, this.meta_defaults));
    let retryCount = 0;
    const retryLimit = 3; // #New: Define retry limit
    const retryLog = () => {
      this.manager.log(logEntry, (err) => {
        if (err && retryCount < retryLimit) {
          retryCount++;
          retryLog();
        } else {
          callback(err, true);
        }
      });
    };
    retryLog();
    this.emit('logged');
  }

