import { Transform } from "stream";
import { JsonValue, JsonParseOptions } from "./baseDefinitions";

export function stringifyWithIndentation(
    options: JsonParseOptions = { pretty: false, indent: 2 },
): Transform {
    return new Transform({
        readableObjectMode: true,
        writableObjectMode: true,
        transform(chunk: JsonValue, encoding, callback) {
            callback(
                undefined,
                options.pretty
                    ? JSON.stringify(chunk, null, options.indent)
                    : JSON.stringify(chunk),
            );
        },
    });
}
